import './ReviewFetcher.css';

import React, { useState, useEffect } from 'react';

const GoogleReviews = () => {
  const [reviews, setReviews] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false)

  useEffect(() => {
    // Define the URL of your backend route
    const backendUrl = 'http://localhost:8000/google-reviews'; // Update with your server URL

    // Fetch the data from the backend route
    fetch(backendUrl)
      .then((response) => response.json())
      .then((data) => setReviews(data))
      .then(setIsLoaded(true))
      .catch((error) => console.error('Error fetching reviews:', error));
  }, []);

  console.log()

  if(isLoaded) {
    return (
      <div>
        <h2>Google Reviews</h2>
        <ul>
          {reviews.map((review, index) => (
            <li key={index}>
              <strong>{review.author_name}:</strong> {review.text}
            </li>
          ))}
        </ul>
      </div>
    );
  };
  }

export default GoogleReviews;
