import './Reviews.css';

import React, { useState, useEffect } from 'react';
import reviews from './revs.json'
import StarRating from '../Stars';
import defaultimg from './default.jpg'
import AntelopeValleyMap from '../GoogleMap';
import BusinessProfile from '../BusinessProfile';

const Reviews = () => {

  const [reviewsList, setReviewsList] = useState([]);

  let allData = reviews.data
  let usedData = [];

  console.log(allData)

  function removeAndPush(arrayOfObjects) {
    // Loop to remove and push the first 10 objects
    for (let i = 0; i < 10; i++) {
      if (arrayOfObjects.length > 0) {
        const removedObject = arrayOfObjects.shift(); // Remove the first object
        usedData.push(removedObject); // Push it to the new array
      } else {
        // If the original array is empty, break the loop
        break;
      }
    }

    return;
  }

  setReviewsList(()=> removeAndPush(allData))
  console.log(reviews)

  return (
    <>
      <div className='review-map-details-container'>
        <BusinessProfile />
        <AntelopeValleyMap />
      </div>
      <div className='reviews-intro'>
        <h3>Here is a list of all our google reviews! if you would like to check them out on the official google business profile or to leave a review of your own,<a className='reviews-click-here' href='https://www.google.com/search?q=tahar+garage+door+repair+services&oq=&aqs=chrome.1.35i39i362l8.1003j0j15&sourceid=chrome&ie=UTF-8#lrd=0x80c259d77e3936f1:0xacaf1a48ea067316,1,,,,' target='_Blank' rel="noreferrer"> Click Here</a></h3>
      </div>
      {
        reviewsList.map((review => (
          <div key={review.author_id} className='reviews-container'>
            <div className='name-star-container'>
              <img className='reviews-author-image' src={review.author_image} alt={defaultimg}></img>
              <p className='review-author'>{review.author_title}</p>
              <StarRating className='review-stars' rating={review.rating} />
            </div>
            <p className='reviews-date'>Date posted: {review.review_datetime_utc}</p>
            <p className='reviews-text'>{review.review_text}</p>
          </div>
        )))
      }
    </>
  );
};

export default Reviews;
