import './ReviewsApi.css';
import React, { useState, useEffect } from 'react';

const ReviewsApi = () => {
  const [businessData, setBusinessData] = useState(null);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    // Fetch the Google Business Profile data from the backend
    fetch('http://localhost:8000/api/business-profile')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setBusinessData(data))
      .catch((error) => console.error('Error fetching data:', error));

    // Fetch the reviews data from the backend
    fetch('http://localhost:8000/api/business-reviews')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setReviews(data))
      .catch((error) => console.error('Error fetching reviews:', error));
  }, []);

  // Function to render star ratings
  const renderStars = (rating) => {
    const starCount = Math.round(rating);
    const stars = [];

    for (let i = 0; i < 5; i++) {
      if (i < starCount) {
        stars.push(<span key={i} className="star-filled">★</span>);
      } else {
        stars.push(<span key={i} className="star-empty">☆</span>);
      }
    }

    return stars;
  };

  return (
    <div>
      {businessData ? (
        <div className="business-profile-container">
          <h2 className="business-name">
            <a
              href={`https://www.google.com/maps/place/?q=place_id:${businessData.place_id}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {businessData.name}
            </a>
          </h2>
          <p>Address: {businessData.formatted_address}</p>
          <a className="business-phone" href="tel:661-202-8255">
            661-202-8255
          </a>
          <p className="rating">
            Rating: {renderStars(businessData.rating)}
            <br />
            ({businessData.user_ratings_total} reviews)
          </p>
          {businessData.photos && businessData.photos.length > 0 && (
            <a
              href={`https://www.google.com/maps/place/?q=place_id:${businessData.place_id}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              <img
                className="business-photo"
                src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${businessData.photos[0].photo_reference}&key=AIzaSyD254Fwgs-RkYHwu9J5yzkzfULuRloRPNE`}
                alt="Business Profile"
              />
            </a>
          )}
          {/* Add more details as needed */}
        </div>
      ) : (
        <p>Loading...</p>
      )}

      {reviews.length > 0 ? (
        <div className="reviews-container">
          <h3>Reviews</h3>
          <ul>
            {reviews.map((review, index) => (
              <li key={index} className="review">
                <p>{review.text}</p>
                <p className="review-rating">
                  Rating: {renderStars(review.rating)}
                </p>
                <p className="review-author">By: {review.author_name}</p>
              </li>
            ))}
          </ul>
        </div>
      ) : null}
    </div>
  );
};

export default ReviewsApi;
