import React, { useState, useEffect } from 'react';
import './HomePage.css';

import ImageCarousel from '../ImageCarousel';
import BusinessProfile from '../BusinessProfile';
import AntelopeValleyMap from '../GoogleMap';
import ReviewsApi from '../ReviewsApi';

import image1 from './bannerImages/AdobeStock_1.jpeg'
import image2 from './bannerImages/AdobeStock_2.jpeg'
import image3 from './bannerImages/AdobeStock_3.jpeg'
import image4 from './bannerImages/AdobeStock_4.jpeg'

import repairMan from './bannerImages/repairMan.jpg'

const HomePage = () => {
  const [word, setWord] = useState('springs');
  const [fadeOut, setFadeOut] = useState(false);

  useEffect(() => {
    const words = [' Springs', ' Doors', ' Tracks', ' Openers', ' Cables', ' Sensors', ' Remotes', ' Off Track'];
    let index = 1;

    const timer = setInterval(() => {
      setFadeOut(true); // Trigger fade-out animation
      setTimeout(() => {
        setWord(words[index]);
        setFadeOut(false); // Reset fade-out animation
        index = (index + 1) % words.length;
      }, 1000); // Change the word after 500ms (0.5 seconds)
    }, 4000); // Repeat every 2 seconds (2000 milliseconds)

    return () => clearInterval(timer); // Cleanup on unmount
  }, []);

  let images = [image1, image2, image3, image4];
  let phoneNumber = (<a className='home-page-phone-link hover:text-blue-700' href='tel:661-202-8255'> 661-202-8255</a>)

  return (
    <div className='home-page-container'>

      <h2 className='absolute w-full flex place-content-center text-xl  z-10 bg-sky-400/75 rounded-lg p-1 md:text-5xl'>
        Call ''{phoneNumber}'' now for help with -{' '}
        <span className={`animated-word ${fadeOut ? 'fade-out' : ''}`}>
          {word}
        </span>
      </h2>

      <div className='home-page-content-container'>

        <div>
          <ImageCarousel images={images} />
        </div>

        <div className='home-page-details-demographics'>
          <div className='home-page-details-text'>
            <div className='home-page-details-1'>
              <img className='repair-man-img' alt='' src={repairMan}></img>
              <div>
                <h3 className='home-page-details-title'>Tahar Garage Door Services in Palmdale & Lancaster</h3>
                <p className='home-page-details-p'>Tahar Garage Door Services provides excellent customer service, specializing in all of your garage door service Cneeds. Weâ€™ve got you covered from fixing a minor mishap to installing a completely new garage door! We offer free estimates in a timely manner. We only use high-quality materials so they will withstand all seasons, daily use, and remain durable. All materials and labor provided include a warranty. Give us a call today! Family-owned and operated!
