import './Header.css';
import image from './TAHAR.png';
import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import '../../dist/output.css'

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="bg-blue-900 text-white py-4">
      <div className="container mx-auto flex flex-col items-center justify-between md:flex-row md:items-center">
        <Link to="/">
          <img className="w-12 h-auto mb-4 md:mb-0" src={image} alt="LOGO" />
        </Link>

        <button
          className="md:hidden bg-blue-500 hover:bg-blue-600 text-white px-2 py-1 rounded-lg"
          onClick={toggleMenu}
        >
          {isMenuOpen ? 'Close' : 'Menu'}
        </button>

        <nav
          className={`${
            isMenuOpen ? 'block' : 'hidden md:block'
          } space-y-2 md:space-x-4 md:space-y-0 text-center md:flex md:items-center`}
        >
          <Link className="hover:text-blue-300" to="/about">
            About Us
          </Link>
          <Link className="hover:text-blue-300" to="/reviews">
            Reviews
          </Link>
          <Link className="hover:text-blue-300" to="/services">
            Services
          </Link>
          <Link className="hover:text-blue-300" to="/gallery">
            Gallery
          </Link>
          <Link className="hover:text-blue-300" to="/faq">
            FAQ
          </Link>
          <Link className="hover:text-blue-300" to="/contact">
            Contact
          </Link>
        </nav>

        <div className="text-sm text-center md:text-left">
          <p className="mb-2">Ask about our senior citizen discount!</p>
          <p className="mb-2">mention how you found our site for a discount!</p>
        </div>
      </div>

      <div className="container mx-auto py-4 text-center">
        <a
          className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full inline-block"
          href="tel:661-202-8255"
        >
          Call Now For A Free Quote! 661-202-8255
        </a>
      </div>
    </header>
  );
};

export default Header;
