import React, { useState, useEffect } from 'react';
import reviews from './revs.json';
import StarRating from '../Stars';
import defaultimg from './default.jpg';
import AntelopeValleyMap from '../GoogleMap';
import BusinessProfile from '../BusinessProfile';

const Reviews = () => {
  const [reviewsList, setReviewsList] = useState([]);
  const [loadedReviews, setLoadedReviews] = useState(0); // Track the number of loaded reviews

  // Load the first 10 reviews initially
  useEffect(() => {
    loadMoreReviews();
  }, []);

  const loadMoreReviews = () => {
    const remainingReviews = reviews.data.slice(loadedReviews, loadedReviews + 10);
    setReviewsList((prevReviews) => [...prevReviews, ...remainingReviews]);
    setLoadedReviews(loadedReviews + 10);
  };

  return (
    <>
      {/* ... (existing JSX code) ... */}
      <button onClick={loadMoreReviews}>Load More Reviews</button>
    </>
  );
};

export default Reviews;
