import React, { Component } from 'react';

class GoogleBusinessProfile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      businessInfo: null,
      error: null,
    };
  }

  componentDidMount() {
    // Replace 'YOUR_BACKEND_URL' with the URL of your backend server
    const backendUrl = 'http://localhost:3001'; // Use the correct URL and port

    // Replace 'PLACE_ID' with the place ID of the business you want to display
    const placeId = 'ChIJ8TY5ftdZwoARFnMG6kgar6w';

    fetch(`${backendUrl}/business-profile/${placeId}`)
      .then((response) => response.json())
      .then((data) => {
        if (!data.error) {
          this.setState({ businessInfo: data });
        } else {
          this.setState({ error: 'Error fetching business information' });
        }
      })
      .catch((error) => {
        this.setState({ error: 'Error fetching business information' });
      });
  }

  // Render method remains the same as in the previous example

  render() {
    const { businessInfo, error } = this.state;

    if (error) {
      return <div>{error}</div>;
    }

    if (!businessInfo) {
      return <div>Loading...</div>;
    }

    return (
      <div>
        <h2>{businessInfo.name}</h2>
        <p>Rating: {businessInfo.rating}</p>
        <p>Phone: {businessInfo.formatted_phone_number}</p>
        <p>Address: {businessInfo.formatted_address}</p>
        {businessInfo.website && (
          <p>
            Website: <a href={businessInfo.website} target="_blank" rel="noopener noreferrer">{businessInfo.website}</a>
          </p>
        )}
      </div>
    );
  }
}

export default GoogleBusinessProfile;
