// export default BusinessMap;

initMap = () => {
  const { business } = this.state;

  // Replace with the actual latitude and longitude of the business
  const businessLocation = { lat: business.latitude, lng: business.longitude };

  const map = new window.google.maps.Map(this.googleMapRef.current, {
    zoom: 14,
    center: businessLocation,
  });

  // Create a polygon to outline the area
  const polygonCoordinates = [
    { lat: business.latitude - 0.01, lng: business.longitude - 0.01 },
    { lat: business.latitude - 0.01, lng: business.longitude + 0.01 },
    { lat: business.latitude + 0.01, lng: business.longitude + 0.01 },
    { lat: business.latitude + 0.01, lng: business.longitude - 0.01 },
  ];

  const polygon = new window.google.maps.Polygon({
    paths: polygonCoordinates,
    strokeColor: '#FF0000', // Outline color (red in this example)
    strokeOpacity: 0.8,
    strokeWeight: 2,
    fillColor: '#FF0000', // Fill color (red in this example)
    fillOpacity: 0.35,
  });

  polygon.setMap(map);

  // Add a marker for the business location
  new window.google.maps.Marker({
    position: businessLocation,
    map: map,
    title: business.name,
  });
};
