import './ReviewsApi.css';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import google from '../BusinessProfile/google.png'

const ReviewsApi = () => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    // Fetch the reviews data from the backend
    fetch('http://localhost:8000/api/business-profile')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setReviews(data.reviews))
      .catch((error) => console.error('Error fetching reviews:', error));
  }, []);

  console.log(reviews)

  // Function to render star ratings
  const renderStars = (rating) => {
    const starCount = Math.round(rating);
    const stars = [];

    for (let i = 0; i < 5; i++) {
      if (i < starCount) {
        stars.push(<span key={i} className="star-filled">★</span>);
      } else {
        stars.push(<span key={i} className="star-empty">☆</span>);
      }
    }

    return stars;
  };

  return (
    <div className="bg-blue-900 rounded-t-2xl p-2">
      <h3 className='text-black text-3xl underline mb-4'>Recent Reviews</h3>
      <ul>
        {reviews.map((review, index) => (
          <li key={index} className="review-api">
            <p>{review.text}</p>
            <div className='flex'>
              <div>
                <p className="review-rating-api">
                  Rating: {renderStars(review.rating)}
                </p>
                <p className="review-author-api">By: {review.author_name}</p>
              </div>
              <a
                href={`https://www.google.com/maps/place/Tahar+Garage+Door+Repair+Services/@34.5989272,-118.1815931,17z/data=!3m1!4b1!4m6!3m5!1s0x80c259d77e3936f1:0xacaf1a48ea067316!8m2!3d34.5989272!4d-118.1815931!16s%2Fg%2F11jwjffm8q?entry=ttu`}
                target="_blank"
                rel="noopener noreferrer"
              >
                <img className='h-12 custom-google-business-link' src={google}></img>
              </a>
            </div>
          </li>

        ))}
      </ul>
      <Link className='header-link' to='/reviews'> View All Reviews</Link>
    </div>
  );
};

export default ReviewsApi;
