import React, { useState, useEffect } from 'react';
import reviews from './revs.json';
import StarRating from '../Stars';
import defaultimg from './default.jpg';
import AntelopeValleyMap from '../GoogleMap';
import BusinessProfile from '../BusinessProfile';
import { useModal } from "../../context/Modal";
import OpenModalButton from '../OpenModalButton';
import OpenLargeImg from '../OpenLargeImg';

const Reviews = () => {
  const [reviewsList, setReviewsList] = useState([]);
  const [loadedReviews, setLoadedReviews] = useState(10); // Start with the first 10 reviews

  // Add state for the modal
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState('');

  useEffect(() => {
    // Load the initial reviews when the component mounts
    setReviewsList(reviews.data.slice(0, 10));
  }, []);

  const loadMoreReviews = () => {
    if (loadedReviews >= reviews.data.length) {
      // No more reviews to load
      return;
    }

    // Load 10 more reviews when the "Load More Reviews" button is clicked
    const nextReviews = reviews.data.slice(loadedReviews, loadedReviews + 10);
    setReviewsList((prevReviews) => [...prevReviews, ...nextReviews]);
    setLoadedReviews(loadedReviews + 10);
  };

  const openModal = (image) => {
    setSelectedImage(image);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <>
      <div className='review-map-details-container'>
        <BusinessProfile />
        <AntelopeValleyMap />
      </div>
      <div className='reviews-intro'>
        <h3>Here is a list of all our google reviews! if you would like to check them out on the official google business profile or leave a review of your own,<a className='reviews-click-here' href='https://www.google.com/search?q=tahar+garage+door+repair+services&oq=&aqs=chrome.1.35i39i362l8.1003j0j15&sourceid=chrome&ie=UTF-8#lrd=0x80c259d77e3936f1:0xacaf1a48ea067316,1,,,,' target='_Blank' rel="noreferrer"> Click Here</a></h3>
      </div>
      {
        reviewsList.map((review => (
          <div key={review.author_id} className='reviews-container'>
            <div className='name-star-container'>
              <img className='reviews-author-image' src={review.author_image} alt={defaultimg}></img>
              <p className='review-author'>{review.author_title}</p>
              <StarRating className='review-stars' rating={review.rating} />
              {ifImage(review.review_img_urls)}
            </div>
            <p className='reviews-date'>Date posted: {review.review_datetime_utc}</p>
            <p className='reviews-text'>{review.review_text}</p>
          </div>
        )))
      }
      <div className='reviews-loader-container'>
        <button className='reviews-load-more-reviews' onClick={loadMoreReviews}>Load More Reviews</button>
      </div>

      {/* Modal */}
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close-modal" onClick={closeModal}>&times;</span>
            <img className='review-image-large' src={selectedImage} alt='' />
          </div>
        </div>
      )}
    </>
  );
};

export default Reviews;
