import React, { Component } from 'react';

class BusinessMap extends Component {
  constructor(props) {
    super(props);
    this.googleMapRef = React.createRef();
  }

  componentDidMount() {
    const { business } = this.props;

    // Replace 'YOUR_API_KEY' with your actual Google Maps API key
    const apiKey = 'AIzaSyD254Fwgs-RkYHwu9J5yzkzfULuRloRPNE';

    // Load the Google Maps JavaScript API script
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&callback=initMap`;
    script.async = true;
    script.defer = true;
    script.onload = this.initMap;
    document.head.appendChild(script);

    // Initialize the map
    window.initMap = this.initMap;

    this.setState({ business });
  }

  initMap = () => {
    const { business } = this.state;

    // Replace with the actual latitude and longitude of the business
    const businessLocation = { lat: business.latitude, lng: business.longitude };

    const map = new window.google.maps.Map(this.googleMapRef.current, {
      zoom: 14,
      center: businessLocation,
    });

    new window.google.maps.Marker({
      position: businessLocation,
      map: map,
      title: business.name,
    });
  };

  render() {
    return <div ref={this.googleMapRef} style={{ width: '100%', height: '400px' }}></div>;
  }
}

export default BusinessMap;
