import React, { useState, useEffect } from 'react';
import './HomePage.css';

import ImageCarousel from '../ImageCarousel';
import image1 from '../HomePage/1.png';
import image2 from '../HomePage/2.png';
import image3 from '../HomePage/3.png';

const HomePage = () => {
  const [word, setWord] = useState('springs');

  useEffect(() => {
    const words = ['springs', 'doors', 'motors', 'tracks'];
    let index = 0;

    const timer = setInterval(() => {
      setWord(words[index]);
      index = (index + 1) % words.length;
    }, 2000); // Change every 2 seconds (2000 milliseconds)

    return () => clearInterval(timer); // Cleanup on unmount
  }, []);

  let images = [image1, image2, image3];

  return (
    <>
      <h2>
        Call now for help with{' '}
        <span className="animated-word">{word}</span>
      </h2>
      <ImageCarousel images={images} />
    </>
  );
};

export default HomePage;
