import React, { useState, useEffect } from 'react';

const BusinessProfile = () => {
  const [businessData, setBusinessData] = useState(null);

  useEffect(() => {
    // Fetch the Google Business Profile data from your backend
    fetch('/api/business-profile')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setBusinessData(data))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  // Function to render star ratings
  const renderStars = (rating) => {
    const starCount = Math.round(rating);
    const stars = [];

    for (let i = 0; i < 5; i++) {
      if (i < starCount) {
        stars.push(<span key={i} className="star-filled">★</span>);
      } else {
        stars.push(<span key={i} className="star-empty">☆</span>);
      }
    }

    return stars;
  };

  return (
    <div>
      <h1>Google Business Profile Information</h1>
      {businessData ? (
        <div>
          <h2>{businessData.name}</h2>
          <p>Address: {businessData.formatted_address}</p>
          <p>Phone: {businessData.formatted_phone_number}</p>
          <p>
            Rating: {businessData.rating} ({businessData.user_ratings_total} reviews)
            <br />
            {renderStars(businessData.rating)}
          </p>
          {businessData.photos && businessData.photos.length > 0 && (
            <img
              src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${businessData.photos[0].photo_reference}&key=YOUR_GOOGLE_API_KEY`}
              alt="Business Profile"
            />
          )}
          {/* Add more details as needed */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default BusinessProfile;
