import React, { useState } from 'react';
import './Faq.css';

const Faq = () => {
  const [showAnswers, setShowAnswers] = useState(Array(5).fill(false)); // Initialize an array to track answer visibility

  const toggleAnswer = (index) => {
    const updatedAnswers = [...showAnswers];
    updatedAnswers[index] = !updatedAnswers[index];
    setShowAnswers(updatedAnswers);
  };

  return (
    <div className="max-w-lg mx-auto">
      <h1 className="text-2xl font-semibold text-center mb-6">Frequently Asked Questions</h1>

      <div className="space-y-4">
        {[1, 2, 3, 4, 5].map((index) => (
          <div className="bg-white shadow-md rounded p-4" key={index}>
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-semibold">Question {index}: What is Tailwind CSS?</h2>
              <button
                className="text-blue-500"
                onClick={() => toggleAnswer(index - 1)}
              >
                Toggle
              </button>
            </div>
            <p className={`mt-2 ${showAnswers[index - 1] ? '' : 'hidden'}`}>
              Tailwind CSS is a utility-first CSS framework for building modern web applications. It allows you to create custom designs quickly by applying pre-defined classes to your HTML elements.
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Faq;
