import './ReviewFetcher.css';

import React, { Component } from 'react';

class GoogleReviews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      reviews: [],
      error: null,
    };
  }

  componentDidMount() {
    // Replace 'YOUR_API_KEY' with your actual Google Places API key
    const apiKey = 'AIzaSyD254Fwgs-RkYHwu9J5yzkzfULuRloRPNE';
    const placeId = 'ChIJ8TY5ftdZwoARFnMG6kgar6w'; // Replace with the Place ID of the business

    fetch(`https://maps.googleapis.com/maps/api/place/details/json?placeid=${placeId}&fields=reviews&key=${apiKey}`, {
      method: 'GET',
      mode: 'cors',
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.result && data.result.reviews) {
          this.setState({ reviews: data.result.reviews });
        } else {
          this.setState({ error: 'No reviews found for this place.' });
        }
      })
      .catch((error) => {
        this.setState({ error: 'An error occurred while fetching reviews.' });
        console.error(error);
      });
  }

  render() {
    const { reviews, error } = this.state;

    return (
      <div className="google-reviews">
        <h2>Google Reviews</h2>
        {error ? (
          <p>{error}</p>
        ) : (
          <ul>
            {reviews.map((review, index) => (
              <li key={index}>
                <strong>{review.author_name}</strong>
                <p>{review.text}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

export default GoogleReviews;
