import './Header.css';
import image from './TAHAR.png';
import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import '../../dist/output.css';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Close the mobile menu when the screen size changes to desktop
  const handleResize = () => {
    if (window.innerWidth >= 768) {
      setIsMobileMenuOpen(false);
    }
  };

  // Add a resize event listener to handle the screen size change
  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <header className="bg-blue-900 text-white py-4">
      <div className="sm:hidden md:block container mx-auto flex items-center justify-between">
        <Link to="/">
          <img className="w-12 h-auto" src={image} alt="LOGO" />
        </Link>

        {/* Show the button only on screens the size of a mobile device */}
        <button
          className="md:hidden bg-blue-500 hover:bg-blue-600 text-white px-2 py-1 rounded-lg"
          onClick={toggleMobileMenu}
        >
          {isMobileMenuOpen ? 'Close' : 'Menu'}
        </button>

        {/* Show the navigation links as a row on computer screens */}
          <nav className="hidden md:block space-x-4">
            <Link className="hover:text-blue-300" to="/about">
              About Us
            </Link>
            <Link className="hover:text-blue-300" to="/reviews">
              Reviews
            </Link>
            <Link className="hover:text-blue-300" to="/services">
              Services
            </Link>
            <Link className="hover:text-blue-300" to="/gallery">
              Gallery
            </Link>
            <Link className="hover:text-blue-300" to="/faq">
              FAQ
            </Link>
            <Link className="hover:text-blue-300" to="/contact">
              Contact
            </Link>
          </nav>
      </div>

      {/* Show the mobile menu on screens the size of a mobile device */}
      {isMobileMenuOpen && (
        <div className="container mx-auto py-4 text-center">
          <ul className="space-y-2">
            <li>
              <Link
                className="hover:text-blue-300"
                to="/about"
                onClick={toggleMobileMenu}
              >
                About Us
              </Link>
            </li>
            <li>
              <Link
                className="hover:text-blue-300"
                to="/reviews"
                onClick={toggleMobileMenu}
              >
                Reviews
              </Link>
            </li>
            <li>
              <Link
                className="hover:text-blue-300"
                to="/services"
                onClick={toggleMobileMenu}
              >
                Services
              </Link>
            </li>
            <li>
              <Link
                className="hover:text-blue-300"
                to="/gallery"
                onClick={toggleMobileMenu}
              >
                Gallery
              </Link>
            </li>
            <li>
              <Link
                className="hover:text-blue-300"
                to="/faq"
                onClick={toggleMobileMenu}
              >
                FAQ
              </Link>
            </li>
            <li>
              <Link
                className="hover:text-blue-300"
                to="/contact"
                onClick={toggleMobileMenu}
              >
                Contact
              </Link>
            </li>
          </ul>
        </div>
      )}

      <div className="container mx-auto py-4 text-center">
        <a
          className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full inline-block"
          href="tel:661-202-8255"
        >
          Call Now For A Free Quote! 661-202-8255
        </a>
      </div>
    </header>
  );
};

export default Header;
